const fetchSchools = async (educationLevel) => {
  activeTab.value = educationLevel;

  try {
    const response = await axios.get(`/ranking/school-details`, {
      params: {
        page: 1,
        educationLevelName: educationLevel,
      },
    });

    const data = response.data;

    if (data.status === "success") {
      schools.value = data.data;
    } else {
      schools.value = [];
    }
  } catch (error) {
    console.error("Error fetching data:", error);
    schools.value = [];
  }
};

const getRankSvg = (rank) => {
  return rankSvgs[rank - 1] || "<svg></svg>";
};

onMounted(() => {
  fetchSchools(activeTab.value);
});
</script>