const fetchSchools = async (educationLevel) => {
  activeTab.value = educationLevel; // Atur tab aktif ke level pendidikan yang dipilih

  try {
    const response = await axios.get(`/ranking/school-details`, {
      params: {
        page: 1,
        educationLevelName: educationLevel,
      },
    });

    const data = response.data; // Mengambil data dari respons axios

    if (data.status === "success") {
      schools.value = data.data; // Assign the fetched data to schools
    } else {
      schools.value = []; // Clear schools if the fetch fails
    }
  } catch (error) {
    console.error("Error fetching data:", error);
    schools.value = []; // Clear schools in case of an error
  }
};

// Fungsi untuk mendapatkan SVG sesuai rank
const getRankSvg = (rank) => {
  return rankSvgs[rank - 1] || "<svg></svg>"; // Gambar default jika rank tidak ada
};

// Fetch initial data on mount
onMounted(() => {
  fetchSchools(activeTab.value);
});

const getIcon = (tab) => {
  const fillColor = activeTab.value === tab.id ? "#F9F6F1" : "#76685A"; // Warna aktif putih, tidak aktif hitam
  return tab.icon.replace('fill="currentColor"', `fill="${fillColor}"`); // Mengganti warna
};
